import shelterService from '../../../services/shelter-service';
import { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import Paper from '@material-ui/core/Paper';
 


function ShelterDetail() {

    const params = useParams();

    const [shelter, setShelter ] = useState();

    useEffect(() => {

        
        const { id } = params;

        shelterService.detail(id)
            .then(shelter => setShelter(shelter))
            .catch(error => console.error(error))


            return () => {

            }

    }, [params])
     
    if (!shelter) {
        return null;
    }
    
    const { name, pets, email, city, avatar, image, contact, phone, description } = shelter;
    return (
        <div className="container">
            <div className="row">
                <img className='shelter-detail-avatar col-3' alt={name} src={avatar}/>
                <Paper elevation={0} className="col-8">
                    <h4>{name}</h4>
                    <div>
                        <h6><i className="fas fa-envelope me-2"></i>{email}</h6>
                        <h6><i className="fas fa-envelope me-2"></i>{ph}</h6>
                        <h6><i className="fas fa-envelope me-2"></i>{email}</h6>
                    </div>
                </Paper>
            </div>
            
        </div>
    );
}

export default ShelterDetail;
