const PASSWORD_PATTERN = /^.{8,}$/;

const validations = {
    name: (value) => {
        let message;
        if (!value) {
            message = 'A name is required';
        } else if (value.length < 3) {
            message = 'Name needs at least 3 chars'
        } else if (value.length > 50) {
            message = 'Name needs at most 50 chars'
        }
        return message;
    },
    lastName: (value) => {
        let message;
        if (!value) {
            message = 'A last name is required'
        } else if (value > 50) {
            message = 'The last name cant be greater than 50 chars'
        }
        return message;
    },
    email: (value) => {
        let message;
        if(!value) {
            message = 'A valid email is required'
        } else if (!EMAIL_PATTERN.test(value)) {
            message = 'The email is invalid'
        }
        return message;
    },
    password: (value) => {
        let message;
        if(!value) {
            message = 'A valid password is required'
        } else if (!PASSWORD_PATTERN.test(value)) {
            message = 'The password needs at least 8 chars'
        }
        return message;
    },
}

const history = useHistory();

    const [state, setState] = useState({
        shelter: {
            name: '',
            avatar: '',
            email: '',
            phone: '',
            cif: '',
            city: [],
            contact: '',
            description: '',
            password: '',
            rol: 'shelter'
        },
        touch: {},
        errors: {
            name: validations.name(),
            email: validations.email(),
            phone: validations.phone(),
            cif: validations.cif(),
            contact: validations.contact(),
            description: validations.description(),
            password: validations.password(),
        }
    });

    const [selectedPrediction, setSelectedPrediction] = useState(null)
    const [searchValue, setSearchValue] = useState("")
    const {predictions, setPredictions} = usePlacesAutocomplete(searchValue)

    const handlePredictionSelection = (e, prediction) => {
        const geocoder = new window.google.maps.Geocoder();
        geocoder.geocode({ 
            'placeId': prediction.place_id
        }, 
        function(responses, status) {
            console.log(responses)
            if (status == 'OK') {
                var lat = responses[0].geometry.location.lat();
                var lng = responses[0].geometry.location.lng();
                const newState = {
                    ...state,
                    shelter: {
                        ...state.shelter,
                        city: [Number(lat), Number(lng)]
                    }
                }
                setState(newState)
            }
        });
        e.preventDefault()
        setSelectedPrediction(prediction)
        setPredictions()
      }

    const handleChange = (event) => {
        let { name, value } = event.target;

        if (event.target.file) {
            value = event.target.files[0]
        }


        setState(state => ({
            ...state,
            shelter: {
                ...state.shelter,
                [name]: value
            },
            errors: {
                ...state.errors,
                [name]: validations[name] && validations[name](value)
            }
        }));
    }

    const handleBlur = (event) => {
        const { name } = event.target;
        setState(state => ({
            ...state,
            touch: {
                ...state.touch,
                [name]: true
            }
        }));
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log('ENTRA', state)
        
           console.log('ENTRA')
            const { shelter } = state;
            shelterService.register(shelter)
            .then(shelter => {
                setState(shelter)
                history.push('/login')
            })
            .catch(error => {
                const { message, errors } = error && error.response ? error.response.data : error;
                console.error(message);

                setState(state => ({
                    ...state,
                    errors: errors
                }))
            })         
        
    }

    const isValid = () => {
        const { errors } = state;
        return Object.keys(errors).some(error => errors[error]);
    }

    const { shelter, errors, touch } = state;



