import { useParams, useHistory } from 'react-router';
import { useState, useEffect } from 'react';
import petService from '../../../services/pet-service';
import ''


function PetDetail() {

    const history = useHistory();
    const params = useParams();

    const [pet, setPet] = useState();

    useEffect(() => {
        
        const { id } = params;

        petService.detail(id)
            .then(pet => setPet(pet))
            .catch(error => console.error(error))

        return () => {
            // ComponentWillUnmount
        }

    }, [history, params])

    if (!pet) {
        return null
    }

    const { name, race, age, image, gender, personality, shelter, specie, status } = pet;
    return ( 
        <div class="container">
            <div class="profile-header">

                <div class="profile-img">
                    <img src="./bg.jpg" width="200" alt="Profile Image"/>
                </div>

                <div class="profile-nav-info">
                    <h3 class="user-name">Bright Code</h3>
                    <div class="address">
                        <p id="state" class="state">New York,</p>
                        <span id="country" class="country">USA.</span>
                    </div>
                </div>

            </div>
        </div>
    )
}

export default PetDetail;
