import { useParams, useHistory } from 'react-router';
import { useState, useEffect } from 'react';
import petService from '../../../services/pet-service';
import { Link } from 'react-router-dom'
import './PetDetail.css';


function PetDetail() {

    const history = useHistory();
    const params = useParams();

    const [pet, setPet] = useState();

    useEffect(() => {
        
        const { id } = params;

        petService.detail(id)
            .then(pet => setPet(pet))
            .catch(error => console.error(error))

        return () => {
            // ComponentWillUnmount
        }

    }, [history, params])

    if (!pet) {
        return null
    }

    const { name, race, age, image, gender, personality, shelter, status, gallery } = pet;

    const StyledPaper = withStyles({
        root: {
            backgroundColor: '#fafafa',

        }
    })(Paper);
    return ( 

        <div className="container">
            <div className="shelter-info-distribution">
                <div className="d-flex flex-column justify-content-center align-items-center shelter-detail-avatar">
                    <img className='col-6' alt={name} src={avatar}/>
                    <Link className="btn btn-primary shadow-sm col-6 mt-3" to="#" role="button">Visit Website</Link>
                </div>
                <StyledPaper elevation={0} className="col-lg-6 col-sm-12">
                    <h3 className="mb-5 shelter-detail-name">{name}</h3>
                    <div className="shelter-detail-contact col-md-12">
                        <h6 className="me-4 fw-bold"><i className="fas fa-user me-2"></i>{contact}</h6>
                        <h6 className="me-4 fw-bold"><i className="fas fa-envelope me-2"></i>{email}</h6>
                        <h6 className="me-4 fw-bold"><i className="fas fa-phone me-2"></i>{phone}</h6>
                    </div>
                    {/* <h6 className="fw-bold"><i className="fas fa-map-marker-alt me-2"></i>{city}</h6> */}
                </StyledPaper>
            </div>
            <Divider light={false} className="bg-secondary" variant="middle" />
            <p className="shelter-detail-description">{description}</p>
            <div className="row justify-content-center">
                <div className="d-flex justify-content-center">
                    {pets.map(pet => (
                        <div className="d-flex justify-content-center m-4" key={pet.id}><PetItem pet={pet}/></div>
                    ))}
                </div>
            </div>
        </div>




































        /* <div className="container">


            <div className="profile-header col-6">

                <div className="profile-img">
                    <img src={image} width="200" alt={name}/>
                </div>

                <div className="profile-nav-info">
                    <h3 className="user-name">{name}</h3>
                    <div className="address">
                        <p>{race.toUpperCase()}</p>
                    </div>
                </div>

            </div>

            
        
            <div className="profile-side col-6">
                <div className="d-flex justify-content-end">
                    <h5 className={status === 'Looking for home' ? 'profile-side-home' : 'profile-side-adopted'}>{status}</h5>
                </div>
                <div className="d-flex justify-content-around align-items-center pet-detail-info">
                    <p><i className="fas fa-birthday-cake"></i> {age} {age === 1 ? 'Year' : 'Years'}</p>
                    <p><i className={gender === 'male' ? 'fas fa-mars' : 'fas fa-venus'}></i> {gender}</p>
                    <p><i className="fas fa-paw"></i><Link to={`/shelters/${shelter.id}`} className="pet-shelter-link"> {shelter.name}</Link></p>
                </div>
                
                <div className="pet-personality">
                    <p>{personality}</p>
                </div>
                <div className="profile-btn">
                    <Link className="btn pet-detail-btn" to='/'><i className="fas fa-paper-plane"></i> Send Adoption Request</Link>
                    <Link className="btn pet-detail-btn" to='/pets'><i className="fas fa-undo"></i> Back to Pets</Link>
                </div>
            </div>
            {gallery.length !== 0 ? (
            <div className="mt-5">
                <h3>Here you can see more about<span className="pet-name-gallery"> {name}</span></h3>
                <div className="pet-detail-gallery">
                    {gallery.map(img => <img key={img} src={img} alt={name}/>)}
                </div>
            </div>
            ) : ''}
            
                
            
        </div> */
    )
}

export default PetDetail;
