import { useState } from 'react';


function PetFilter({ className, onSearch, loading }) {

    const [filter, setFilter] = useState('');

    const handleChange = (event) => {
        const { value } = event.target
        setFilter(value);
        onSearch(value);
    }


    return (






        {/* <div className={`row row-cols-1 ${className}`}>
            <div className="col">
                <div className="input-group mb-2">
                    <span className="input-group-text">
                        <i className={`fa fa-${loading ? 'refresh fa-spin': 'search'}`}></i>
                    </span>
                    <input type="text" name="race" className="form-control" placeholder="Search by race..."
                        value={filter} onChange={handleChange} />
                </div>
            </div>
        </div> */}
    );
}


PetFilter.defaultProps = {
    loading: false,
    className: '',
    onSearch: () => {}
}



export default PetFilter;
