import './Petitem.css';
import { Link } from 'react-router-dom';


function PetItem( { pet:{ id, name, image, age, gender, status, shelter } }) {


    return (
        <div className="col-4">
            <div className="card">
                <div className="img-container">
                    <div className="d-flex justify-content-between align-items-center p-2 first"> <span className={status === 'Looking for home' ? 'love-status' : 'adopted-status'}>{status}</span> <span className="wishlist"><i className="fa fa-heart"></i></span> </div> <img alt={name} src={image} className="img-fluid"/>
                </div>
                <div className="product-detail-container">
                    <div className="d-flex justify-content-between align-items-center">
                        <h6 className="mb-0">{name}</h6> <span className="text-danger font-weight-bold">{`${age} years`}</span>
                    </div>
                    <div className="d-flex justify-content-between align-items-center mt-2">
                        <div className="ratings"> <i className={gender === 'male' ? 'fa fa-mars' : 'fa fa-venus'}></i> <span>{gender}</span> </div>
                    </div>
                    <div className="mt-3"> <button className="btn btn-danger btn-block"><Link to={`/pets/${id}`}>{`More about ${name}`}</Link></button> </div>
                </div>
            </div>
        </div>
    );
}

export default PetItem;
