import shelterService from '../../../services/shelter-service';
import { useState, useEffect } from 'react';
import { useParams } from 'react-router';
 


function ShelterDetail() {

    const params = useParams();

    const [shelter, setShelter ] = useState();

    useEffect(() => {

        
        const { id } = params;

        shelterService.detail(id)
            .then(shelter => setShelter(shelter))
            .catch(error => console.error(error))


            return () => {

            }

    }, [params])
     
    if (!shelter) {
        return null;
    }
    
    const { name, pets, email, city, avatar, image, contact, phone, description } = shelter;
    return (
        <div className="container">
            <div>
                <img className='shelter-detail-avatar rounded' alt={name} src={avatar}/>
                
            </div>
            
        </div>
    );
}

export default ShelterDetail;
