import { Link } from 'react-router-dom';
import './ShelterItem.css';
import Paper from '@material-ui/core/paper';

function ShelterItem({ shelter: {id, name, city, avatar, pets}}) {
    return(
        <div className="d-flex align-items-center justify-content-around">
            <div className="justify-content-start">
                <img className="shelter-img-container" src={avatar} alt={name}/>
            </div>
            <Paper elevation={3}>
                <h5>{name}</h5>
                <h5>{city}</h5>
                <h5>{pets.map((pet, i) => <div key={i}><img className="shelter-pet-img" src={pet.image}/></div>)}</h5>
            </Paper>
        </div>
        
        
    );
}

export default ShelterItem;

