import shelterService from '../../../services/shelter-service';
import { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import Paper from '@material-ui/core/Paper';
import './ShelterDetail.css';
import Divider from '@material-ui/core/Divider';
 


function ShelterDetail() {

    const params = useParams();

    const [shelter, setShelter ] = useState();

    useEffect(() => {

        
        const { id } = params;

        shelterService.detail(id)
            .then(shelter => setShelter(shelter))
            .catch(error => console.error(error))


            return () => {

            }

    }, [params])
     
    if (!shelter) {
        return null;
    }
    
    const { name, pets, email, city, avatar, image, contact, phone, description } = shelter;
    return (
        <div className="container">
            <div className="d-flex justify-content-center align-items-center mb-5">
                <div className="text-center shelter-detail-avatar">
                    <img className='col-6' alt={name} src={avatar}/>
                </div>
                <Paper elevation={0} className="col-6">
                    <h3 className="mb-5">{name}</h3>
                    <div className="d-flex align-items-center justify-content-start">
                        <h6 className="me-4"><i className="fas fa-user me-2"></i>{contact}</h6>
                        <h6 className="me-4"><i className="fas fa-envelope me-2"></i>{email}</h6>
                        <h6 className="me-4"><i className="fas fa-phone me-2"></i>{phone}</h6>
                    </div>
                    <p className="mt-4"><i className="fas fa-map-marker-alt me-2"></i>{city}</p>
                </Paper>
            </div>
            <Divider light={false} variant="middle" />
        </div>
    );
}

export default ShelterDetail;
