import { Link } from 'react-router-dom';
import './ShelterItem.css';
import Paper from '@material-ui/core/paper';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';

function ShelterItem({ shelter: {id, name, city, avatar, pets}}) {
    

    const useStyles = makeStyles({
        root: {
          maxWidth: 345,
        },
      });

      const classes = useStyles();
    return(
        <div className="d-flex align-items-center justify-content-around mb-5">
            <Card className={classes.root}>
                <CardActionArea>
                    <CardMedia
                        component="img"
                        alt={name}
                        height="140"
                        image={avatar}
                        title="Contemplative Reptile"
                        />
                    <CardContent>
                        <p gutterBottom variant="h5" component="h2">{name}</p>
                        <p variant="body2" color="textSecondary" component="p">
                            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
                            across all continents except Antarctica
                        </p>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary">
          Share
        </Button>
        <Button size="small" color="primary">
          Learn More
        </Button>
      </CardActions>
    </Card>
        </div>
        
        
    );
}

export default ShelterItem;

