import { useEffect } from 'react';
import { useHistory } from 'react-router-dom';




const ScrollToTop = ({  }) => {
    const history = useHistory();
    useEffect(() => {
        const unlisten = history.listen(() => {
            window.scrollTo(0, 0);
        });
        return () => {
            history.location.hash === "" && unlisten();
        }
    }, [history]);
    return (null);
}



export default withRouter(ScrollToTop);
