import sheltersService from '../../services/shelter-service';
import { Component } from 'react';


class SheltersList extends Component {
    
    state = {
        shelters: [],
        loading: true
    }

componentDidMount() {
        
        sheltersService.list()
            .then(shelters => this.setState({
                shelters,
                loading: false
            }))
            .catch(error => console.error(error));
    }

    render() {
        const { shelters } = this.state;

        if(this.state.loading) {
            return <img src="https://res.cloudinary.com/getapet/image/upload/v1617958203/dog_paw_auipy7.jpg" alt="paw" width="5rem"/>
        }

        return (
            <div>
                {shelters.map(shelter => (
                    <div key={shelter.id}>{shelter.name}</div>
                ))}
            </div>
        );
    }
}







export default SheltersList;
