import http from './base-api-service';

const list = (race) => {
    const options = {}

    if(race) {
        options.params = {
            race
        }
    }

    return http.get('/pets', options)
};

const detail = (id) => http.get(`/pets/${id}`);

const create = (pet) => http.post('/pets')

const update = (pet) => http

const service = {
    list,
    detail,
    create
}



export default service;
