export const googleAutocomplete = async text =>
  new Promise((resolve, reject) => {
    if (!text) {
      return reject("Need valid text input")
    }

    
    if (typeof window === "undefined") {
      return reject("Need valid window object")
    }

    try {
      const geocoder = new window.google.maps.Geocoder();

      const service = new window.google.maps.places.AutocompleteService();
      const request = { input: text };
     const predictions =  service.getPlacePredictions(request) 
        .then(prediction => {
         return prediction.map(item => {
            geocoder.geocode({'placeId': item.place_id})
              .then(geocode => {
                return {
                  ...item,
                  geocode
                }
              })
          })
        })

      /* new window.google.maps.places.AutocompleteService().getPlacePredictions(
        { input: text, componentRestrictions: { country: "es" } },
        resolve
      ) */
    } catch (e) {
      reject(e)
    }
  })
