        <div className="container d-flex justify-content-center">
            <form className="login shadow col-lg-4 col-md-8 col-sm-12">
                <div className="input-group mb-4 d-flex justify-content-center align-items-end">
                    <span><i className="fas fa-user fa-lg me-3"></i></span>
                    <input type="text" placeholder="Email..." className='w-75 login-form-control login-form-control-underlined'/>
                    <div className="invalid-feedback"></div>
                </div>
                <div className="input-group mb-4 d-flex justify-content-center align-items-end">
                    <span><i className="fas fa-lock fa-lg me-3"></i></span>
                    <input type="password" placeholder="Password..." className='w-75 login-form-control login-form-control-underlined border-primary '/>
                    <div className="invalid-feedback"></div>
                </div>
                <div className="justify-content-center">
                    <button className="mt-5 btn login-btn"><Link className="login-btn-link" to='/register'>Register</Link></button>
